    public function getSourceContext(): Source
    {
        return new Source("{% extends 'base.html.twig' %}

{% block stylesheets %}
    {{ parent() }} {# Garde les styles de base définis dans base.html.twig #}
    <link rel=\"stylesheet\" href=\"{{ asset('css/inscription.css') }}\">
{% endblock %}

{% block body %}
    <!-- Conteneur principal -->
    <div class=\"auth-container\">
        <!-- Bouton retour (en dehors des boîtes) -->
        <button onclick=\"window.history.back()\" class=\"back-btn\">Retour</button>

        <!-- Section de Connexion -->
        <section class=\"auth-box\">
            <h2>Connexion</h2>

            {% if error %}
                <p class=\"error\">Erreur : {{ error.messageKey|trans(error.messageData, 'security') }}</p>
            {% endif %}

            <form action=\"{{ path('login') }}\" method=\"post\">
                {{ csrf_token('authenticate') }}

                <label for=\"login-email\">Email</label>
                <input type=\"email\" id=\"login-email\" name=\"_username\" value=\"{{ last_username }}\" required>

                <label for=\"login-password\">Mot de passe</label>
                <input type=\"password\" id=\"login-password\" name=\"_password\" required>

                <button type=\"submit\" class=\"btn\">Se connecter</button>
                <p class=\"switch-text\">Pas encore de compte ? <a href=\"{{ path('register') }}\" class=\"switch-link\">S'inscrire</a></p>
            </form>
        </section>
    </div>
{% endblock %}
", "security/login.html.twig", "/Users/brice/Desktop/formation CDA/Symfony/templates/security/login.html.twig");
